class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, res = 0, n = s.size();
        int state = (s[n-1] == '0') ? 0 : 1;
        for (int i = n - 2; i >= 0; i--) {
            if (state == 0) {
                if (s[i] == '1'){
                    cnt += 1;
                    state = 1;
                }
            }
            else {
                if (s[i] == '0') {
                    state = 0;
                }
            }
            if (s[i] == '1') {
                res += cnt;
            }
        }
        return res;
    }
};
