class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int p=0;
        bool check=0; 
        vector<int> vec; 
        int n=s.size();
        for(int i=0;i<n;i++){ 
            if(s[i]=='1'){ 
                vec.push_back(1); 
                check=1; 
            } 
            if(s[i]=='0' and check==1){ 
                vec.push_back(0); 
                check=0; 
            } 
        } 
        for(int i=0;i<vec.size();i++){ 
            if(vec[i]==1)p++; 
            else ans=ans+p; 
        } 
        return ans; 

    }
};
