class Solution:
    def maxOperations(self, s: str) -> int:
        a = b = 0
        has_zero = False
        for char in s:
            if char == "1":
                if has_zero:
                    b += a
                    has_zero = False
                a += 1
            else:
                has_zero = True
        return b + a if has_zero else b
