class Solution {
public:
    int maxOperations(string s) {
        int res=0,a=0,i=0,n=s.size();
        while(i<n){
            char c=s[i];
            int cnt=0;
            while(i<n && s[i]==c){
                i++;
                cnt++;
            }
            if(c=='1'){
                a+=cnt;
            }
            else{
                res+=a;
            }
        }
        return res;
    }
};
