int maxOperations(char* s) {
    int result = 0;
    int head_count = 0;


    int index=0;
    bool lastZero = false;
    while(s[index]){
        if(s[index] == '1'){
            head_count++;
            lastZero = false;
        }else if(!lastZero){
            //s[index] == '0'
            result += head_count;
            lastZero = true;
        }

        index++;
    }

    return result;
}
