class Solution {
public:
    int maxOperations(string s) {
        int res = 0, on = 0;
        bool chk = true;

        for (int i = 0; i < s.size(); i++) {
            if (s[i] == '1') {
                on++;
                chk = true;
            } else {
                if (chk) res += on;
                chk = false;
            }
        }

        return res; 
    }
};
