class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int c0=0,c1=0,ans=0;
       for(int i=0;i<n-1;i++){
           if(s[i]=='0')c0++;
           else{
               if(c1>0 && c0>0)ans+=c1;
               c1++;
               c0=0;
           }
       }
        if(c0>0 || s[n-1]=='0')ans+=c1;
        return ans;
    }
};
