class Solution {
public:
    int maxOperations(string s) {
        int n= s.size(), i=0, an= 0, t=0;
        while(i<n){
            int c= 0;
            while(i<n && s[i]=='1')   c++, i++;
            t+= c;
            if(i!= n){
                an+= t;
                while(i<n && s[i]== '0')    i++;
            }
        }
        return an;
    }
};
