class Solution {
public:
    int maxOperations(string s) {
        int res=0,c=0,t=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='0') {
                if(t) res+=c;
                t=0;
            }
            else{
                c++;
                t=1;
            }
        }
        return res;
    }
};
