class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int res = 0;
        int end = -1;
        for (int i = n - 1; i >= 0; i--) {
            if (s.charAt(i) == '0') {
                end = i;
                break;
            }
        }
        if (end == -1) {
            return 0;
        }
        int cnt = 1;
        int next = 0;
        for (int i = end - 1; i >= 0; i--) {
            if (s.charAt(i) == '1') {
                res += cnt;
                next = 1;
            }
            else {
                if (next == 1) {
                    cnt++;
                }
                next = 0;
            }
        }
        return res;
    }
}
