class Solution {
    public int maxOperations(String s) {
        int ans = 0, p = 0;
        boolean k = false;
        ArrayList<Integer> h = new ArrayList<>();

        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == '1') {
                h.add(1);
                k = true;
            }
            if (s.charAt(i) == '0' && k) {
                h.add(0);
                k = false;
            }
        }

        for (int it : h) {
            if (it == 1) {
                p++;
            } else {
                ans += p;
            }
        }

        return ans;
    }

    }
