class Solution {
    func maxOperations(_ s: String) -> Int {
        let sLen = s.count
        let sArray = Array(s)
        var ret: Int = 0
        var curIdx: Int = 0
        var curOneNum: Int = 0
        while curIdx < sLen {
            if sArray[curIdx] == "1" {
                curOneNum += 1
            } else if curIdx == sLen-1 || sArray[curIdx+1] == "1" {
                ret += curOneNum
            }
            curIdx += 1
        }
        return ret
    }
}
