class Solution {
    func maxOperations(_ s: String) -> Int {
        let n = s.count
        var operations = 0
        var flag = false
        var countOnes = 0
        for i in s.indices {
            processCharacter(s[i], &countOnes, &operations, &flag)
        }
        return operations
    }
    private func processCharacter(_ char: Character, _ countOnes: inout Int, _ operations: inout Int, _ flag: inout Bool) {
        if char == "1" {
            handleOne(&countOnes, &flag)
        } else if char == "0" {
            handleZero(&countOnes, &operations, &flag)
        }
    }
    private func handleOne(_ countOnes: inout Int, _ flag: inout Bool) {
        countOnes += 1
        flag = true
    }
    private func handleZero(_ countOnes: inout Int, _ operations: inout Int, _ flag: inout Bool) {
        if countOnes > 0 && flag {
            operations += countOnes
            flag = false
        }
    }
}
