class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        count = 0
        for j in range(len(s)):
            if s[j] == '1':
                if j > 0 and s[j-1] == '0':
                    ans += count
                count += 1
        if s[-1] == '0':
            ans += count
        return ans
