class Solution:
    def maxOperations(self, s: str) -> int:
        ans =0
        cnt =0 
        i =0
        while i<len(s):
            if s[i]=='1':
                cnt+=1
            flag = False
            while i<len(s) and s[i]=='0':
                flag = True
                i+=1
            if flag:
                ans+=cnt
            else:
                i+=1
        return ans
