class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int moves = 0;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i) == '1'){
                count++;
            }else{
                moves+=count;
                while(i+1<s.length() && s.charAt(i+1) != '1'){
                    i++;
                }
            }
        }
        return moves;
    }
}
