impl Solution {
    pub fn max_operations(s: String) -> i32 {
    let mut t = vec![];

    {
        let mut pre = '_';
        for c in s.chars() {
            if c == '1' {
                t.push(c);
            } else {
                if pre != '0' {
                    t.push(c);
                }
            }
            pre = c;
        }

    }
    let mut cnt1 = 0;
    let mut ans = 0;
    for i in 0..t.len() {
        if t[i] == '0' {
            ans += cnt1;
        } else {
            cnt1 += 1;
        }
    }

    ans
}

}
