class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int i=n-1;

        int ans = 0;
        int cnt = 0;

        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                while(i<n && s[i]=='1'){
                    cnt++;
                    i++;
                }
                i--;
                // cout<<i<<" "<<cnt<<endl;
                if(i<n-1)
                 ans+=cnt;
            }
        }
        // cout<<endl;

        return ans;
    }
};
