class Solution {
public:
    int maxOperations(string s) {
        int count = 0;
        int total = 0;

        for(int i = 0; i < s.size(); i++){
            if(s[i] == '1' && s[i + 1] == '0'){
                total = total + 1 + count;
            }

            count += (s[i] == '1');
        }

        return total;
    }
};
