class Solution {
public:
    int maxOperations(string s) {
        int ans=0,tempans=0;
    bool flag=true;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
            flag=true;
            tempans++;
            }
            else if(flag &&s[i]=='0'){
            ans=ans+tempans;
            flag=false;
            }
        }
        return ans;
    }
};
