class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int f = 0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                f++;
            }
            else{
                while(i<s.size()&&s[i]=='0'){
                    i++;
                }
                ans+=f;
                i--;
            }
        }
        return ans;
    }
};
