class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        boolean found = false;
        int ans = 0, count = 0;
        for(int i = 0; i < n; i++){
            int c = s.charAt(i) - '0';
            switch(c){
                case 0:
                    if(found) ans += count;
                    found = false;
                    break;
                case 1:
                    count++; found = true;
                    break;
                default:
                    throw new RuntimeException("Shouldnt be here");
            }
        }
        return ans;
    }
}
