class Solution {
public:
    int maxOperations(string s) {
        int num_one = 0, num_move = 0;
        bool moved = false;

        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '1') {
                num_one += 1;
                moved = false;
            } else if (!moved) {
                num_move += num_one;
                moved = true;
            }
        }

        return num_move;
    }
};
