class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        vector<int> a;
        int b = 0;
        for (auto ss : s) {
            if (ss == '1') {
                a.push_back(b);
                b = 0;
            } else {
                b = 1;
            }
        }
        if (s[s.length() - 1] == '0') {
            a.push_back(1);
        }
        for (int i = 0; i < a.size(); i++) {
            ret += (i * a[i]);
        }
        return ret;
    }
};
