class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        rights = [0] * n
        sufixSum = [0]*n
        cnt = 0
        ans = 0
        for i in range(n-1, -1, -1):
            if i==n-1: 
                continue
            if s[i] == '1':
                if s[i+1] =='0':
                    cnt += 1
                ans += cnt
            # print(i, cnt, ans)
        return ans
