class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int cnt=0;
        int res=0;
        bool flag=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                cnt++;
                flag=1;
            }
            else{
                if(flag){

                    res+=cnt;
                    }

                    flag=0;
            }
        }
        return res;
    }
};
