class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int cnt = 0;
        int ans = 0;
        for (int i = 0; i + 1 < n; ++i) {
            if (s.charAt(i) == '1') {
                ++cnt;
                if (s.charAt(i + 1) == '0') {
                    ans += cnt;
                }
            }
        }
        return ans;
    }
}
