class Solution {
public:
    int maxOperations(string s) {
        int ans=0,cnt=0,p=-1;
        int n=s.size();
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                if(i-p-1>0)
                ans+=cnt;
                p=i;
                cnt++;
            }
        }
        if(n-p-1>0){
            ans+=cnt;
        }
        return ans;
    }
};
