class Solution {
    public int maxOperations(String s) {
        int ct=0,sum=0;
        for(int i=0;i<s.length();i++){
            int val=s.charAt(i)-'0';
            if(val==1) ct++;
            else{
                // int j=i+1;
                while(i+1<s.length()&&s.charAt(i+1)=='0') i++;
                sum+=ct;
            }
        }
        return sum;
    }
}
