class Solution {
public:
    int maxOperations(string s) {

        int cnt=0,ans=0;

        for(int i=0;i<s.size();i++){
            if(s[i]=='0'){
                ans+=cnt;
                int j=i;
                while(j<s.size() && s[j]=='0')j++;
                if(i!=j)i=j-1;
            }
            else
                cnt++;
        }

        return ans;

    }
};
