function maxOperations(s: string): number {
    s = s + '1';
    const arr = s.split('');
    let ans = 0;
    for(let i = 0, count = 0; i <= arr.length - 1; count += +(arr[i++] === '1')) {
        if (i - 1 <= -1) continue;
        if (arr[i - 1] === '1') continue;
        if (arr[i] === '0') continue;
        ans += count;
        //console.log(ans, i);
    }
    return ans;
};
