class Solution {
    public int maxOperations(String s) {
        int cnt=0,ans=0;
        int n=s.length();
        char[] arr=s.toCharArray();
        for(int i=n-1;i>0;i--){
            if(arr[i]=='0' && arr[i-1]=='1'){
                //System.out.print(i+" ");
                cnt++;
                ans+=cnt;
            }else if(arr[i]=='1' && arr[i-1]=='1'){
                //System.out.print(i+" ");
                ans+=cnt;
            }
        }
        // System.out.println(Arrays.toString(arr));
        // System.out.println(cnt);
         //System.out.println();
        return ans;
    }
}
