class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0;
        int idx = 0, n = s.size();
        if (n == 1) return 0;
        // 每次1换完位置以后，所有已经换好的可以全部再换一遍
        int cntone = 0;
        while (idx < n){
            while (idx + 1 < n && !(s[idx] == '1' && s[idx+1] == '0')){
                if (s[idx] == '1') cntone++;
                idx++;
            }
            int oneidx = idx;
            if (idx + 1 == n) break;
            cnt += cntone + 1;
            cntone++;
            while (idx + 1 < n && s[idx + 1] != '1') idx++;
            swap(s[oneidx], s[idx]);
            idx++;
        }
        return cnt;
    }
};
