class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.size(), i = 0, oneCount = 0;
        for(i;i<n;i++){

            if(s[i] == '1'){

                oneCount++;
            }
            else{
                int prv = i;
                while(i<n && s[i] == '0'){
                    prv = i;
                    i++;
                }
                i--;
                ans+=oneCount;
            }
        }
        return ans;
    }
};
