class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int cntOne=0;
        int cntTotalOne=0,ans=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1'){
                if(cntOne==0){
                    ans+=cntTotalOne;
                }
                cntOne++;

            }
            else {
                cntTotalOne+=cntOne;
                cntOne=0;
            }
        }
        if(s[n-1]=='0'){
            ans+=cntTotalOne;
        }
        return ans;
    }
};
