class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        vector<int> cnts;
        int cnt=0;
        bool flag = true;
        for(int i=s.length()-1;i>=0;i--){
            if(s[i]=='0'&& flag){
                cnt++;
                flag = false;
            }
            else if(s[i]=='1' ){
                flag = true;
                cnts.push_back(cnt);
            }
        }
        for(auto x:cnts){
            res+=x;
        }
        return res;
    }
};
