class Solution {
public:
  int maxOperations(std::string s) {
    int total_ops = 0;
    int num_ones_held = 0;
    for (int i = 0; i < s.size() - 1; i++) {
      num_ones_held += (s[i] == '1') ? 1 : 0;
      total_ops += (s[i] == '1' && s[i+1] == '0') ? num_ones_held : 0;
    }

    return total_ops;
  }
};
