class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int cnt = 0;

        // Count the number of '1's in the string
        for(int i = n - 1; i >= 0; i--) {
            if(s[i] == '1') {
                cnt++;
            }
        }

        // If the string contains only '1's or no '1's, return 0
        if(cnt == 0 || cnt == n) {
            return 0;
        }

        std::vector<int> v;
        int k = n - 1;

        // Find the rightmost '0'
        while(k >= 0 && s[k] == '1') {
            k--;
        }


        for(int i = 0; i < k; i++) {
            if(s[i] == '1') {
                int j = i + 1;
                while(j < k && s[j] == '1') {
                    j++;
                }
                v.push_back(j - i);
                i = j - 1;
            }
        }

        // for(int i=0;i<v.size();i++){
        //     cout<<v[i]<<" ";
        // }
        if(v.size()==0){
            return 0;
        }
        if(v.size() == 1) {
            return v[0];
        }

        cnt = 0;

        for(int i = 1; i < v.size() ; i++) {
            cnt += v[i-1];
            v[i] += v[i-1];
        }
        int m=v.size();
        //cout<<cnt<<" "<<v[m-1]<<" "; 
        return cnt + v[m-1];
        return 0;
    }
};
