class Solution {
public:
    int maxOperations(string s) {
        long long sum=0,cnt=0,cur=0;
        for(int i=0;i<s.size();i++){
            if(s[i]=='1'){
                cnt++;
            }else{
                if(cnt){
                    cur+=cnt;
                    sum+=cur;
                    cnt=0;
                }
                //cout<<sum<<endl;
            }
        }
        return sum;
    }
};
