class Solution {
public:
    int maxOperations(string s) {
        bool hasOne = false;
        vector<int> sequence;
        for (char ch : s) {
            if (ch == '1') {
                sequence.push_back(1);
                hasOne = true;
            } else if (ch == '0' && hasOne) {
                sequence.push_back(0);
                hasOne = false;
            }
        }

        int countOnes = 0, totalOperations = 0;
        for (int value : sequence) {
            if (value == 1) {
                countOnes += 1;
            } else {
                totalOperations += countOnes;
            }
        }

        return totalOperations;
    }
};
