class Solution {
public:
    int maxOperations(std::string s) {
        int count = 0;
        int ans = 0;
        int n = s.size();
        for(int i = 0;i<n;i++){
              if(i>1&&s[i]=='1'&&s[i-1]=='0'){
                ans+=count;
                count++;
            }else if(s[i]=='1'){
                count++;
            }

        }
          if(s[n-1]=='0'){
                ans+=count;
            }
        return ans;
    }
};
