class Solution {
  public int maxOperations(String s) {
    int ones = 0;
      int ans = 0;
      for (int i = 0; i < s.length(); i++) {
          if (s.charAt(i) == '1') ones++;
          else {
              int z = i;
              while (z + 1 < s.length() && s.charAt(z + 1) == '0') z++;
              ans += (ones);
              i = z;
          }
      }
      return ans;
  }
}
