class Solution {
public:
    int maxOperations(string s) {
        int ans=0,n=s.length();
        int cnt=0;
        for(int l=0;l<n;l++){
            if(s[l]=='1') cnt++;
            else{
                while(l<n && s[l]=='0') l++;
                if(cnt!=0){
                    ans+=cnt;
                }
                l--;
            }
            // cout<<l<<" "<<cnt<<endl;
        }
        return ans;
    }
};
