class Solution {
    public int maxOperations(String s) {
        int ans=0;
        int co=0;
        boolean isOne=false;
        for(char c: s.toCharArray()){
            if(c=='1'){
                co++;
                isOne=true;
            }
            else if(c=='0'&& isOne){
                ans+=co;
                isOne=false;
            }
        }
        return ans;
    }
}
