class Solution {
    public int maxOperations(String s) {
        int res = 0;
        int nums = 0;
        String[] split = s.split("0");
        for (String string : split) {
            if (string.isEmpty()) {
                continue;
            }
            res += nums;
            nums += string.length();

        }
        if (s.charAt(s.length() - 1) == '0') {
            res += nums;
        }
        return res;
    }
}
