class Solution
{
public:
    const static int maxn = 1e5 + 10;
    const static int maxm = 1e5 + 10;
    const static long long mod = 1e9 + 7;
    const long long INF_LL = 0x3f3f3f3f3f3f3f3f;
    const int INF = 0x3f3f3f3f;
    int maxOperations(string s)
    {
        int n = s.length();
        int ans = 0;
        int pre1 = 0;
        for (int i = 0; i < n; ++i)
        {
            if (i > 0 && s[i - 1] == '0' && s[i] == '0')
                continue;
            if (s[i] == '0')
            {
                ans += pre1;
            }
            else if (s[i] == '1')
            {
                pre1++;
            }
        }
        return ans;
    }
};
