impl Solution {
pub fn max_operations(s: String) -> i32 {
    let s: Vec<u32> = s.chars().filter_map(|c| c.to_digit(10)).collect();

    let mut res = 0;
    let mut cnt = 0;

    for i in 0..s.len() {
        if s[i] == 1 {
            cnt += 1;
        } else {
            if i + 1 >= s.len() || s[i + 1] == 1 {
                res += cnt;
            }
        }
    }

    res
}
}
