class Solution(object):
    def maxOperations(self, s):
        """
        :type s: str
        :rtype: int
        """
        block = []
        accu = 1
        for i in range(1,len(s)):
            if s[i]!=s[i-1]:
                block.append(accu)
                accu = 1
            else:
                accu += 1

        block.append(accu)


        if s[-1]=='1':
            block.pop()

        if block and s[0]=='0':
            block.pop(0)

        ans = 0
        accu = 0
        for num in block[::2]:
            accu += num
            ans += accu



        return ans
