class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ope = 0;
        int cnt1 = 0;
        for(int i=0;i<n-1;i++){
            if(s[i] == '0') continue;
            else{
                cnt1++;
                if(s[i+1] == '0'){
                    ope += cnt1;
                }
            }
        }
        return ope;
    }
};
