class Solution {
    public int maxOperations(String s) {
        int oneCnt = 0, ans = 0;
        boolean hasZero = false;

        for(char c: s.toCharArray()) {
            if(c == '1') {
                if(hasZero) ans += oneCnt;
                hasZero = false;
                oneCnt++;
            } else if(oneCnt > 0) {
                hasZero = true;
            }
        }

        return ans + (s.charAt(s.length() - 1) == '0' ? oneCnt : 0);
    }
}
